/*! LazyYT (lazy load Youtube videos plugin) - v0.1.5 - 2013-01-13

 * Usage:
<div class="lazyYT" data-youtube-id="laknj093n" data-width="300" data-height="200">loading...</div>

* Copyright (c) 2014 Tyler Pearson; Licensed MIT

* Usage with ZURB Foundation: <div class="js-lazyYT flex-video widescreen" data-youtube-id="_oEA18Y8gM0" data-width="100%" data-height="">Loading...</div> *

* Modified by Ryan Norman on 1-27-14 to enable comparison of both highest quality preview images provided by YouTube.  Chooses the highest as preview.

* Thanks to Suman Bogati via stackoverflow.com for the actual comparison portion. */

;(function($) {
  'use strict';

  var titleStyle = ['z-index:100!important', 'color:#fff!important', 'font-family:sans-serif!important', 'font-size:12px!important', 'top:10px!important', 'left:12px!important', 'position:absolute!important', 'margin:0!important', 'padding:0!important', 'line-height:1!important', 'font-style:normal!important', 'font-weight:normal!important'].join(';'),
      buttonStyle = ['position:absolute!important', 'margin-left:-30px!important', 'margin-top:-20px!important', 'z-index:100!important', 'left:50%!important', 'top:50%!important', 'width:60px!important'].join(';'),
    buttonImage = 'data:image/png;base64,';

  function setUp(el) {
      var $el = el,
        width = $el.data('width'),
        height = $el.data('height'),
        id = $el.data('youtube-id');

      /******* Stuff for compairing *********/
      var img1 = new Image();
      img1.src = "http://img.youtube.com/vi/" + id + "/0.jpg";
      img1.onload = function() {
          compareWidth(this.width);
        }

      var img2 = new Image();
      img2.src = "http://img.youtube.com/vi/" + id + "/maxresdefault.jpg";
      img2.onload = function() {
          compareWidth(this.width);
        }

      var w1;
      var compareWidth = function(width) {
          //here you can do operation with width and height
          if (typeof w1 == 'undefined') {
            w1 = width;
          }
          else {
            if (w1 <= width) {
              var finalchoice = "http://img.youtube.com/vi/" + id + "/maxresdefault.jpg";
              previewDisplay(finalchoice);
            }
            else {
              var finalchoice = "http://img.youtube.com/vi/" + id + "/0.jpg";
              previewDisplay(finalchoice);
            }
          }
        }
        /****** end *******/

      var previewDisplay = function(choice) { //Added
          if (typeof width === 'undefined' || typeof height === 'undefined' || typeof id === 'undefined') {
            throw new Error('lazyYT is missing a required data attribute.');
          }

          $el.css({
            'position': 'relative',
            'height': height,
            'width': width,
            'background': "url(" + choice + ") center center no-repeat",
            //modified
            'cursor': 'pointer',
            '-webkit-background-size': 'cover',
            '-moz-background-size': 'cover',
            '-o-background-size': 'cover',
            'background-size': 'cover'
          }).html('<p style="' + titleStyle + '" id="lazyYT-title-' + id + '" class="lazyYT-title"></p><img class="lazyYT-button-loaded" style="' + buttonStyle + '" src="' + buttonImage + '" />').addClass('lazyYT-image-loaded'); // modified with new class lazyYT-button-loaded

          $.getJSON('https://gdata.youtube.com/feeds/api/videos/' + id + '?v=2&alt=json', function(data) {
            $('#lazyYT-title-' + id).text(data.entry.title.$t);
          });

          $el.on('click', function(e) {
            e.preventDefault();
            if (!$el.hasClass('lazyYT-video-loaded') && $el.hasClass('lazyYT-image-loaded')) {
              $el.html('<iframe width="' + width + '" height="' + height + '" src="//www.youtube.com/embed/' + id + '?autoplay=1" frameborder="0" allowfullscreen></iframe>').removeClass('lazyYT-image-loaded').addClass('lazyYT-video-loaded'); 
            }
          });

        }
    } //Added
    
  $.fn.lazyYT = function() {
      return this.each(function() {
        var $el = $(this).css('cursor', 'pointer');
        setUp($el);
      });
    };
    
}(jQuery));